<?php
    include 'config.php';
    session_start();

    $_SESSION['id_client'] = 1;
    $id_client_connecte = $_SESSION['id_client'];

    try {
    $stmt = $pdo->prepare("
        SELECT 
            cc.nom,
            cc.prenom,
            cc.date_naissance,
            cc.adresse_mail AS email,
            cc.num_tel AS telephone,
            id.mdp AS mot_de_passe,
            a.adresse,
            a.code_postal,
            a.ville,
            a.pays
        FROM public.compte_client cc
        LEFT JOIN public.identifiants id
            ON cc.id_num = id.id_num
        LEFT JOIN public.adresse a
            ON cc.id_client = a.id_client
        WHERE cc.id_client = :id_client
    ");

    $stmt->execute(['id_client' => $id_client_connecte]);

    $user = $stmt->fetch(PDO::FETCH_ASSOC);

    if (!$user) {
        die("Utilisateur introuvable.");
    }

    } catch (PDOException $e) {
        die("Erreur SQL : " . $e->getMessage());
    }
?>

